import React, { useState } from 'react'

const Content = () => {
  const [isModalOpen, setIsModalOpen] = useState(false)
  const [data, setData] = useState([])
  const [selectedItemIndex, setSelectedItemIndex] = useState(null)
  const [isEdit, setIsEdit] = useState(false);


  const handleModalOpen = () => {
    setIsModalOpen(true)
  }

  const handleModalClose = () => {
    setIsModalOpen(false)
    setSelectedItemIndex(null)
  }

  const handleFormSubmit = (event) => {
    event.preventDefault()
    const formData = new FormData(event.target)
    const newItem = {
      name: formData.get('name'),
      email: formData.get('email'),
      contact: formData.get('contact'),
      address: formData.get('address'),
      city: formData.get('city'),
      gender: formData.get('gender'),
      hobby: formData.get('hobby'),
      age: formData.get('age'),
    }
    if (selectedItemIndex === null) {
      setData([...data, newItem])
    } else {
      const newData = [...data]
      newData[selectedItemIndex] = newItem
      setData(newData)
    }
    setIsModalOpen(false)
    setSelectedItemIndex(null)
  }

  const handleEdit = (index) => {
    setSelectedItemIndex(index)
    setIsModalOpen(true)
  }

  const handleDelete = (index) => {
    const newData = [...data]
    newData.splice(index, 1)
    setData(newData)
  }
  const handleCancel = () => {
    setSelectedItemIndex(null);
    setIsEdit(false);
  };


  return (
    <>
      <div className='content-container'>
        <div className='modal-row'>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Contact</th>
                <th>Address</th>
                <th>City</th>
                <th>Gender</th>
                <th>Hobby</th>
                <th>Age</th>
                <th><button onClick={handleModalOpen}>Add</button></th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => (
                <tr key={index}>
                  <td>{item.name}</td>
                  <td>{item.email}</td>
                  <td>{item.contact}</td>
                  <td>{item.address}</td>
                  <td>{item.city}</td>
                  <td>{item.gender}</td>
                  <td>{item.hobby}</td>
                  <td>{item.age}</td>
                  <td>
                    <button onClick={() => handleEdit(index)}><i class="fa-sharp fa-solid fa-user-pen"></i></button>
                    <button onClick={() => handleDelete(index)}><i class="fa-solid fa-trash"></i></button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      {isModalOpen && (
        <div className='modal'>
          <div className='modal-content'>
            <span className='close-button' onClick={handleModalClose}></span>
            <form onSubmit={handleFormSubmit}>
              <label>
                Name:
                <input type='text' name='name' pattern="[A-Za-z ]+" defaultValue={selectedItemIndex !== null ? data[selectedItemIndex].name : ""} required />
              </label>
              <label>
                Email:
                <input type='email' name='email' defaultValue={selectedItemIndex !== null ? data[selectedItemIndex].email : ""} required />
              </label>
              <label>
                Contact:
                <input type='number' name='contact' pattern="[0-9]{10}" defaultValue={selectedItemIndex !== null ? data[selectedItemIndex].contact : ""} required min={Math.pow(10, 9)} max={Math.pow(10, 10) - 1} />
              </label>
              <label>
                Address:
                <input type='text' name='address' defaultValue={selectedItemIndex !== null ? data[selectedItemIndex].address : ""} required />
              </label>
              <label>
                City:
                <select name='city' defaultValue={selectedItemIndex !== null ? data[selectedItemIndex].city : ""} required>
                  <option value='rajkot'>Rajkot</option>
                  <option value='Junagadh'>Junagadh</option>
                  <option value='Surat'>Surat</option>
                  <option value='Mumbai'>Mumbai</option>
                </select>
              </label>
              <label>
                Gender:
                <select name='gender' defaultValue={selectedItemIndex !== null ? data[selectedItemIndex].gender : ""} required>
                  <option value='male'>Male</option>
                  <option value='female'>Female</option>
                  <option value='other'>Other</option>
                </select>
              </label>

              <label className='hobby'>
                Hobby:
                <input type='checkbox' name='hobby' value='reading' defaultChecked={selectedItemIndex !== null ? data[selectedItemIndex].hobby === 'reading' : false} />
                Reading
                <input type='checkbox' name='hobby' value='writing' defaultChecked={selectedItemIndex !== null ? data[selectedItemIndex].hobby === 'writing' : false} />
                Writing
                <input type='checkbox' name='hobby' value='swimming' defaultChecked={selectedItemIndex !== null ? data[selectedItemIndex].hobby === 'swimming' : false} />
                Swimming
              </label>

              <label>
                Age:
                <input type='number' name='age' min="1" max="150" defaultValue={selectedItemIndex !== null ? data[selectedItemIndex].age : ""} required />
              </label>
              <button type='submit'>{selectedItemIndex !== null ? "Update" : "Submit"}</button>
              
            </form>
          </div>
        </div>
      )}
    </>
  )
}

export default Content
