import React,{useState} from "react";
import Logo from "../img/Alogo.svg";

export default function Loginimg() {

    const [showPassword, setShowPassword] = useState(false);
    const [userId, setUserId] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
  
    const handleTogglePassword = () => {
      setShowPassword(!showPassword);
    };
  
    const handleLogin = (e) => {
      e.preventDefault();
      // Perform login validation here
      if (userId === "") {
        setError("Please enter user ID");
      } else if (password === "") {
        setError("Please enter password");
      } else {
        // Perform login logic here
        // You can make API calls or perform any other validation as per your application's requirements
        // If login is successful, navigate to next page or perform other actions
        setError("");
      }
    };
  return (
    <>
      <div className="Logo">
        <div className="side-bar">
          <img src={Logo} width={150} /> <br />
          <form action="">
            <input
              type="text"
              className="Login-input"
              placeholder="Enter user id"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
            />
            <div className="form_item">
              <input type={showPassword ? "text" : "password"} placeholder="Password..." className="Login-input" id="password"  value={password}
                onChange={(e) => setPassword(e.target.value)}/>
              <i className="fa-solid fa-eye" id="eye"  onClick={handleTogglePassword}></i>
              <button type="submit" classname="btn btn-outline-primary d-flex justify-content-end">Login</button>
            </div>
            {error && <p>{error}</p>}
          </form>
        </div>
      </div>
    </>
  );
}
